import {Sidebar, Menu, Header, Wrapper, Main, BodyWrapper, CalendarWrapper, CalendarHeader, CalendarBody, CalendarButton} from './HomeSub.js';
import React, { useState } from 'react';
import {Link} from 'react-router-dom';

const Body = () => {
  var today = new Date();
  const [yearMonth, setYearMonth] = useState({year: today.getFullYear(), month: today.getMonth()+1});

  const Calendar = () => {
    const prevLast = new Date(yearMonth.year, yearMonth.month-1, 0);
    const currLast = new Date(yearMonth.year, yearMonth.month, 0);
    const days = ['일', '월', '화', '수', '목', '금', '토']
    const numWeeks = (currLast.getDate() + prevLast.getDay()+1 + (6-currLast.getDay()))/7;
    const [dates, setDates] = useState([]);

    const clickPrev = (e) => {
      if (yearMonth.month === 1){
        setYearMonth({year: yearMonth.year-1, month: 12});
      }else{
        setYearMonth({year: yearMonth.year, month: yearMonth.month-1});
      }
    };

    const clickNext = (e) => {
      if (yearMonth.month === 12){
        setYearMonth({year: yearMonth.year+1, month: 1});
      }else{
        setYearMonth({year: yearMonth.year, month: yearMonth.month+1});
      }
    };

    return (
      <CalendarWrapper className="calendar">
        <CalendarHeader className="calendarHeader">
          <div>{yearMonth.year}-{yearMonth.month}</div>
          <button className="calender prev" onClick={clickPrev}>prev</button>
          <button className="calender prev" onClick={clickNext}>next</button>
        </CalendarHeader>
        <CalendarBody>
        {
          days.map((day)=>{
            return
            <div> {day} </div>
          })
        }
        </CalendarBody>
      </CalendarWrapper>
    );
  };

  const TodoList = ({day}) => {
    return (
      <div className="todo">todo list</div>
    );
  };

  return (
    <BodyWrapper className="body">
      <Calendar /><TodoList day={today.getDate()}/>
    </BodyWrapper>
  );
};

function Home() {
  const side = [
    {name: "menu1", path: "/menu1"},
    {name: "menu2", path: "/menu2"},
    {name: "menu3", path: "/menu3"}
  ];

  return (
    <Wrapper>
        <Sidebar className="sidebar">
          {side.map((side, index)=>{
            return (
              <Link to={side.path} key={index}>
                <Menu className={side.name}>{side.name}</Menu>
              </Link>
            );
          })}
        </Sidebar>
        <Main>
          <Header className="header"><h2>Header</h2></Header>
          <Body />
        </Main>
    </Wrapper>
  );
}

export default Home;
