import {Sidebar, Menu, Header, Wrapper, Main, BodyWrapper, CalendarWrapper, CalendarHeader, CalendarBody, CalendarButton, CalendarWeek, TodoWrapper, TodoDate} from './HomeSub.js';
import React, { useState } from 'react';
import {Link} from 'react-router-dom';

const Body = () => {
  var today = new Date();
  const [yearMonth, setYearMonth] = useState({year: today.getFullYear(), month: today.getMonth()+1, date: today.getDate()});
  const [todo, setToDo] = useState([]);

  const clickDate = (e, last) => {
    alert(last);
    if (e.target.id > 0 && e.target.id <= last){
      setYearMonth({...yearMonth, date:e.target.id})
    }
  };

  const Calendar = () => {
    const prevLast = new Date(yearMonth.year, yearMonth.month-1, 0);
    const currLast = new Date(yearMonth.year, yearMonth.month, 0);
    const days = ['일', '월', '화', '수', '목', '금', '토']

    const clickPrev = () => {
      if (yearMonth.month === 1){
        setYearMonth({year: yearMonth.year-1, month: 12});
      }else{
        setYearMonth({year: yearMonth.year, month: yearMonth.month-1});
      }
    };

    const clickNext = () => {
      if (yearMonth.month === 12){
        setYearMonth({year: yearMonth.year+1, month: 1});
      }else{
        setYearMonth({year: yearMonth.year, month: yearMonth.month+1});
      }
    };

    return (
      <CalendarWrapper className="calendar">
        <CalendarHeader className="calendarHeader">
          <div>{yearMonth.year}-{yearMonth.month}</div>
          <button className="calender prev" onClick={clickPrev}>prev</button>
          <button className="calender prev" onClick={clickNext}>next</button>
        </CalendarHeader>
        <CalendarBody>
        <CalendarWeek>
        {
          days.map((day)=>{
                return <CalendarButton> {day} </CalendarButton>
            })
        }
        </CalendarWeek>
        {
          [0,1,2,3,4,5].map((week)=>{
            return <CalendarWeek> {
              [0,1,2,3,4,5,6].map((date)=>{
                return <CalendarButton id={date+week*7-prevLast.getDay()} onClick={(e)=>clickDate(e, currLast.getDate())}> {
                    date+week*7-prevLast.getDay() > 0?
                    (date+week*7-prevLast.getDay() <= currLast.getDate()?
                    date+week*7-prevLast.getDay() :
                    null) :
                    null
                } </CalendarButton>
              })
            } </CalendarWeek>
          })
        }
        </CalendarBody>
      </CalendarWrapper>
    );
  };

  const TodoList = () => {
    return (
      <TodoWrapper>
        <div className="todo">todo list</div>
        <TodoDate>
        {yearMonth.month}-{yearMonth.date}
        </TodoDate>
      </TodoWrapper>
    );
  };

  return (
    <BodyWrapper className="body">
      <Calendar /><TodoList />
    </BodyWrapper>
  );
};

function Home() {
  const side = [
    {name: "menu1", path: "/menu1"},
    {name: "menu2", path: "/menu2"},
    {name: "menu3", path: "/menu3"}
  ];

  return (
    <Wrapper>
        <Sidebar className="sidebar">
          {side.map((side, index)=>{
            return (
              <Link to={side.path} key={index}>
                <Menu className={side.name}>{side.name}</Menu>
              </Link>
            );
          })}
        </Sidebar>
        <Main>
          <Header className="header"><h2>Header</h2></Header>
          <Body />
        </Main>
    </Wrapper>
  );
}

export default Home;
