import axios         from 'axios';

import config        from './config.json';
import { getCookie } from '../COMMON';

const server = process.env.NODE_ENV === 'development' ? 'http://192.168.0.3:8000' : 'http://'+config.host+':'+config.port;
//axios.defaults.xsrfCookieName = 'csrftoken';
//axios.defaults.xsrfHeaderName = 'X-CSRFTOKEN';

export const tryLogin = (id,pw) => {
    return axios({
        url: server+'/api/token',
        method: 'post',
        data: {
            username: btoa(id),
            password: btoa(pw),
        },
        responseType: 'json',
        withCredentials: true,
        credentials: 'include'
    })
};

export const searchPt = (pt,mode) => {
    return  axios({
        url: server+'/api/search',
        method: 'post',
        headers: {
            'Authorization': `Bearer ${getCookie('token')}`
        },
        data: {
            patientNumber: pt,
            mode: mode
        },
        responseType: 'json',
        withCredentials: true,
        credentials: 'include'
    })
};

export const getPatientList = (year,month,day) => {
    return axios({
        url: server+'/api/search/multiple',
        method: 'post',
        headers: {
            'Authorization': `Bearer ${getCookie('token')}`
        },
        data: {
            year: year,
            month: month,
            day: day
        },
        responseType: 'json'
    })
};

export const getLabList = (pt) => {
    return axios({
        url: `${server}/api/search/${pt}`,
        method: 'post',
        headers: {
            'Authorization': `Bearer ${getCookie('token')}`
        },
        data: {
            patientNumber: pt
        },
        withCredentials: true,
        credentials: 'include'
    })
};

export const saveMemo = (memo) => {
    return axios({
        url: server+'/api/memos/save',
        method: 'post',
        headers: {
            'Authorization': `Bearer ${getCookie('token')}`
        },
        data: {
            memo: memo
        },
        responseType: 'json',
        withCredentials: true,
        credentials: 'include'
    })
};

export const getMemo = () => {
    return axios({
        url: server+'/api/memos',
        method: 'post',
        headers: {
            'Authorization': `Bearer ${getCookie('token')}`
        },
        data: {
            user: getCookie('user')
        }
    })
};

export const deleteMemo = (user_id, seq) => {
    return axios({
        url: server+'/api/memos/delete',
        method: 'post',
        contentType: 'application/json',
        headers: {
            'Authorization': `Bearer ${getCookie('token')}`
        },
        data: {
            user_id: user_id,
            seq: seq
        }
    })
};
