import { useContext }           from 'react';
import styled, {ThemeContext}   from 'styled-components';
import { ResponsiveLine }       from '@nivo/line';

export const NormalButton = (props) => {

    return (
        <ButtonWrapper className='ButtonWrapper' height={props.height} width={props.width} onClick={props.onClick}>
            <Button className={props.className}>
                {props.title}
            </Button>
        </ButtonWrapper>
    );
};

const ButtonWrapper = styled.div`
    height: ${(props)=>props.height};
    width:  ${(props)=>props.width};
`;

const Button = styled.button`
    height: 100%;
    width: 100%;
    cursor: pointer;
`;

export const MenuIcon = (props) => {
    return (      
        <IconWrapper id={props.id} className={props.className} style={{display:'flex'}} onClick={props.onClick} onMouseOver={props.onMouseOver} onMouseLeave={props.onMouseLeave} tabIndex='0' ref={props.forward}>
            {props.children}
        </IconWrapper>  
    );
};

const IconWrapper = styled.li`
    height: 5%;
    cursor: pointer;
    justify-content: center;
    align-items: center;
    border-top-left-radius: 20px;  
    border-bottom-left-radius: 20px; 
    transition: all 0.5s;

    @media only screen and (max-width: 1500px) {
        border-top-left-radius: 15px;  
        border-bottom-left-radius: 15px; 
        height: 7%;
    }

    @media only screen and (max-width: 1050px) {
        display: none;
    }
`;

export const TabBar = (props) => {
    const {headers} = props;

    const onClick = (e,ids) => {
        //console.log(ids);
        props.onFocus(e,ids);
    };
    const themeContext = useContext(ThemeContext);

    const onFocus = (e) => {
        let head = document.getElementsByClassName('TabHeader');
        for(var i=0;i<head.length;i++){
            head[i].style.backgroundColor = themeContext.primaryColor;
        }
        e.currentTarget.style.backgroundColor = themeContext.secondaryColor;
    };

    return(
        <>
        <TabBarWrapper id='TabBar'>
            {headers.length > 0 &&
            headers.map(item=>{
                return(
                <PseudoWrapper key={item.header} >
                    <TabBarHeader id={item.header} className='TabHeader' tabIndex='0' autoFocus onClick={(e)=>onClick(e,item.header)} onFocus={onFocus}>
                        {item.header}                       
                    </TabBarHeader>
                    <div className='Xicon' style={{cursor:'pointer'}} onClick={props.onClick} tabIndex='0'>&#10006;</div>
                </PseudoWrapper>
                );
            })
            }
        </TabBarWrapper>
        </>
    );
};

const TabBarWrapper = styled.div`
    display:flex;
    position: relative;
    height: 4%;
    width: 100%;
    color:white;

`;

const PseudoWrapper = styled.div`
    display:flex;
    justify-content: center;
    align-items: center;
    position: relative;
    height: 100%;
    width: 7%;
    margin-right: 1%;

    @media only screen and (max-width:1920px) {
        width: 10%;

        &.Xicon {
            font-size: 1vw;
        }
    }

    @media only screen and (max-width: 1500px) {
        width: 13%;
    }
`;

const TabBarHeader = styled.button`
    display:flex;
    justify-content: center;
    align-items: center;
    position: relative;
    height: 100%;
    width: 100%;
    background-color: ${(props)=>props.theme.primaryColor};
    color:white;

    border-top-left-radius: 20px;
    border-top-right-radius: 20px;
    cursor: pointer;
