export const CustomTable = (props) => {

    const data = props.data;

    const headers = props.headers;

    const addRow = (result) => {
        Object.keys(result).map(item=>{
            return <TD key={result[item]}>{result[item]}</TD>
        })
    };

    return(
        <TableCode >
            <THEAD>
                <TR>
                    {headers.map(item=>{
                        return <TH key={item}>{item}</TH>
                    })}
                </TR>
            </THEAD>
            <TBODY>
                {data.map(item=>{
                    return( 
                    <TR key={item.day+'--row'} onClick={(e)=>props.onClick(e,item)}>
                        {headers.map(col=>{
                            return <TD key={item.day+'--'+[col]}>{item[col]}</TD>
                        })}
                    </TR>
                )})}
            </TBODY>
        </TableCode>
    );
};

