import React, { useState, useEffect, useContext }               from 'react';
import { ThemeContext }                                         from 'styled-components';
import { ResponsiveCalendar } from '@nivo/calendar';
import { ResponsiveLine } from '@nivo/line';
import { ResponsiveHeatMap } from '@nivo/heatmap';
import { ResponsiveBullet } from '@nivo/bullet';
import { useRecoilState }                                       from 'recoil';
import { ptData, selectedRadio, displayGrid, getPatientList, ptDataList, getMemoList, saveMemo, getMemo, deleteMemo,
         getLabList, activeFav, typeGrid }                      from '../../RECOIL';
import { Wrapper, Header, HeaderTitle, SideWrapper, HeaderButtons, HeaderUtilBtn, FavMenuBtn, RadioWrap,
         HeaderMenuWrapper, ContentWrapper, ContentSide, ContentSideVersion, ContentSideCard, DateWrapper, DateInput, ModalMemoDelete, ModalMemoEdit,
         ContentMain, ContentMainTop, ContentMainTopHalf, ContentMainTopWrapper, ContentMainCalendarRadio, ModalMemoTitle, ModalMemoContent, ModalInnerWrap,
         ContentMainBottom, ContentMainBottomHalf, ModalWrapper, ModalContent, ModalClose, ModalMain, ModalHeader, ModalMemo, ModalMemoAdd, ModalMemoHead,
         ContentBox, SelectBox, SelectThemeBox, GridBox }       from './DashboardStyles.js';
import ArrowForward                                             from '@material-ui/icons/ArrowForward';
import ArrowBack                                                from '@material-ui/icons/ArrowBack';
import Dehaze                                                   from '@material-ui/icons/Dehaze';
import Home                                                     from '@material-ui/icons/Home';
import Sms                                                      from '@material-ui/icons/Sms';
import Settings                                                 from '@material-ui/icons/Settings';
import ArrowLeft from '@material-ui/icons/ArrowLeft';
import ArrowRight from '@material-ui/icons/ArrowRight';
import { MenuIcon, CustomTable, Divider, getCookie }            from '../../COMMON';
import { theme } from '../../THEME/theme.js';

const hdata = [
    {
      "Feature": "Feature I",
      "F1": 35,
      "F1Color": "hsl(318, 70%, 50%)",
      "F2": 87,
      "F2Color": "hsl(237, 70%, 50%)",
      "F3": 1,
      "F3Color": "hsl(32, 70%, 50%)",
      "F4": 57,
      "F4Color": "hsl(279, 70%, 50%)"
    },
    {
      "Feature": "Feature II",
      "F1": 57,
      "F1Color": "hsl(318, 70%, 50%)",
      "F2": 35,
      "F2Color": "hsl(237, 70%, 50%)",
      "F3": 65,
      "F3Color": "hsl(32, 70%, 50%)",
      "F4": 97,
      "F4Color": "hsl(279, 70%, 50%)"
    },
    {
        "Feature": "Feature III",
        "F1": 12,
        "F1Color": "hsl(318, 70%, 50%)",
        "F2": 76,
        "F2Color": "hsl(237, 70%, 50%)",
        "F3": 35,
        "F3Color": "hsl(32, 70%, 50%)",
        "F4": 44,
        "F4Color": "hsl(279, 70%, 50%)"
    },
    {
      "Feature": "Feature IV",
      "F1": 99,
      "F1Color": "hsl(318, 70%, 50%)",
      "F2": 10,
      "F2Color": "hsl(237, 70%, 50%)",
      "F3": 55,
      "F3Color": "hsl(32, 70%, 50%)",
      "F4": 33,
      "F4Color": "hsl(279, 70%, 50%)"
    }
];

const MyResponsiveLine = ({data, textColor,lineColor}) => (
    <ResponsiveLine
        data={data}
        margin={{ top: 20, right: 110, bottom: 50, left: 60 }}
        xScale={{ type: 'point' }}
        yScale={{ type: 'linear', min: '0', max: 'auto', stacked: true, reverse: false }}
        yFormat=" >-.2f"
        markers={[
          {
              axis: 'y',
              value: 100,
              lineStyle: { stroke: '#b0413e', strokeWidth: 2 },
              textStyle: {fill: textColor},
              legend: 'y marker',
              legendOrientation: 'horizontal'
          }
      ]}
        axisTop={null}
        axisRight={null}
        axisBottom={{
            orient: 'bottom',
            tickSize: 5,
            tickPadding: 5,
            tickRotation: -25,
            //legend: 'EMR Date',
            //legendOffset: 36,
            //legendPosition: 'middle'
        }}
        axisLeft={{
            orient: 'left',
            tickSize: 5,
            tickPadding: 5,
            tickRotation: 0,
            //legend: 'Units',
            //legendOffset: -40,
            //legendPosition: 'middle'
        }}
        colors={lineColor}
        pointSize={window.innerWidth < 1300 ? 6 : window.innerWidth < 1400 ? 6 : 10}
        pointColor={{ from: 'color',modifiers:[] }}
        pointBorderWidth={2}
        pointBorderColor={{ from: 'serieColor' }}
        pointLabelYOffset={-12}
        useMesh={true}
        tooltip={(input)=>{
          //console.log(input)
          return (<div style={{backgroundColor: 'white'}}>{input.point.data.yStacked}</div>);
        }}
        theme={{
            textColor:textColor,
            fontSize: window.innerWidth < 1300 ? "8px" : window.innerWidth < 1400 ? "8px" : "12px",
            axis:{
              ticks:{
                text:{
                  fill:textColor,
                }
              }
            }
        }}
        legends={[
            {
                anchor: 'bottom-right',
                direction: 'column',
                fill: textColor,
                justify: false,
                translateX: 100,
                translateY: 0,
                itemsSpacing: 0,
                itemDirection: 'left-to-right',
                itemWidth: 80,
                itemHeight: 20,
                itemOpacity: 0.75,
                symbolSize: window.innerWidth < 1300 ? 10 : window.innerWidth < 1400 ? 10 : 15,
                symbolShape: 'circle',
                symbolBorderColor: 'rgba(0, 0, 0, .5)',
                effects: [
                    {
                        on: 'hover',
                        style: {
                            itemBackground: 'rgba(0, 0, 0, .03)',
                            itemOpacity: 1
                        }
                    }
                ]
            }
        ]}
    />
);

const MyResponsiveHeatMap = ({data, textColor, heatColor}) => (
    <ResponsiveHeatMap
        data={data}
        keys={[
            'F1','F2','F3','F4'
        ]}
        indexBy="Feature"
        colors={heatColor}
        margin={{ top: 40, right: 40, bottom: 40, left: 40 }}
        forceSquare={true}
        axisTop={{ orient: 'top', tickSize: 5, tickPadding: 5, tickRotation: -90, legend: '', legendOffset: 36 }}
        axisRight={null}
        axisBottom={null}
        axisLeft={{
            orient: 'left',
            tickSize: 5,
            tickPadding: 5,
            tickRotation: 0,
            legend: 'Feature',
            legendPosition: 'middle',
            legendOffset: -70,

        }}
        cellOpacity={1}
        cellBorderColor={{ from: 'color', modifiers: [ [ 'darker', 0.4 ] ] }}
        labelTextColor={{ from: 'color', modifiers: [ [ 'darker', 1.8 ] ] }}
        defs={[
            {
                id: 'lines',
                type: 'patternLines',
                background: 'inherit',
