import { atom } from 'recoil';

export const login_ID = atom({
    key: 'username',
    default: ''
});

export const login_PW = atom({
    key: 'password',
    default: ''
});

export const isLoading = atom({
    key: 'status',
    default: false
});

export const currentEle = atom({
    key: 'currentElement',
    default: ''
});

export const subEle = atom({
    key: 'subElement',
    default: ''
});

export const activeTab = atom({
    key: 'tabs',
    default: []
});

export const activeBody = atom({
    key: 'bodyContent',
    default: ''
});

export const ptData = atom({
    key: 'patient',
    default: {
        ID: '',
        name: '',
        sex: '',
        dob: '',
        recent: '',
        next: '',
        physician: '',
        dept: '',
        labdata: []
    }
});

export const ptDataList = atom({
    key: 'patientList',
    default: []
});

export const ptInfo = atom({
    key:'basic_info',
    default: {
        ID: '',
        name: '',
        sex: '',
        dob: '',
        recent: '',
        next: '',
        physician: '',
        dept: ''
    }
})

export const imageReady = atom({
    key: 'ready',
    default: false
});

export const selectedRadio = atom({
    key: 'radio',
    default: '다수'
});

export const searchCondition = atom({
    key: 'searchCondition',
    default: 'patient_id'
});

export const activeFav = atom({
    key: 'bookmark',
    default: ''
});

export const getMemoList = atom({
    key: 'memo',
    default: []
})

export const typeGrid = atom({
    key: 'grid_type',
    default: localStorage.getItem('mdhi_pref') && localStorage.getItem('mdhi_pref').grid ? JSON.parse(localStorage.getItem('mdhi_pref')).grid 
                                                                : '2x2'
});

export const displayGrid = atom({
    key: 'grid_display',
    default: localStorage.getItem('mdhi_pref') && localStorage.getItem('mdhi_pref').panel ? JSON.parse(localStorage.getItem('mdhi_pref')).panel
                                                                 : { first : 'info', second : 'line', third : 'heat', fourth : 'bull' }
});
