import React, {useState, useEffect, useRef} from 'react';
import {Link} from 'react-router-dom';

const MemoModify = ({item}) => {
  const [editable, setEditable] = useState(false);
  const [title, settitle] = useState(item.title);
  const [memo, setmemo] = useState(item.memo);

  const onChangeTitle = (e) => {
    settitle(e.target.value);
  }

  const onChangeMemo = (e) => {
    settitle(e.target.value);
  }

  const onMemoModify = (e, item) => {
    setEditable(true);
  }

  const onClickMemo = ({e, item}) => {

  }

  const onClickCancel = ({e, item}) => {
     setEditable(false);
  }

  return (
    <>
      <div>
        {editable ? (
          <div>
          <input id="title" type="text" value={title} onChange={onChangeTitle}/>
          <input id="memo" type="text" value={memo} onChange={onChangeMemo}/>
          <div><input type="button" value="수정" onClick={(e)=>onClickMemo(e,item)} />
          <input type="button" value="취소" onClick={(e)=>onClickCancel(e,item)} /></div>
          </div>
        ) : (
          <div>
          <div>{`[${new Date(item.reg_time).toLocaleString('ko-KR', { timeZone: 'UTC' })}] ${item.title}`}</div>
          <div>{item.memo}</div>
          <button onClick={(e)=>onMemoModify(e,item)}>수정</button>
          </div>
        )}
      </div>
    </>
  );
}

const Dashboard = () => {
  const memo = [
    {"seq" : 1,
    "user_id" : 'id1',
    "title" : 'title1',
    "memo" : 'memo1',
    "reg_time" : '2021-11-02 13:58:27',
    "edit_time" : '2021-11-03 13:58:27'},
    {"seq" : 2,
    "user_id" : 'id2',
    "title" : 'title2',
    "memo" : 'memo2',
    "reg_time" : '2021-11-03 13:58:27',
    "edit_time" : '2021-11-03 13:58:27'},
    {"seq" : 3,
    "user_id" : 'id3',
    "title" : 'title3',
    "memo" : 'memo3',
    "reg_time" : '2021-11-03 13:58:27',
    "edit_time" : '2021-11-03 13:58:27'}
  ];

  return (
    <div className="sidebar">
      {memo.length > 0 && memo.map((item,idx) =>{
        return (
          <MemoModify item={item} />
        );
      })}
    </div>
  );
}

export default Dashboard;
